package com.example.sliderview;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import com.bumptech.glide.Glide;
import com.smarteist.autoimageslider.SliderView;
import com.smarteist.autoimageslider.SliderViewAdapter;

public class SliderAdapter extends SliderViewAdapter<SliderAdapter.viewhold> {

    public Context context;

    public SliderAdapter (Context context) {
        this.context = context;
    }



    @Override
    public viewhold onCreateViewHolder(ViewGroup parent) {
        View inflate = LayoutInflater.from(parent.getContext()).inflate(R.layout.adapter_ui,null);
         return new viewhold(inflate);


    }

    @Override
    public void onBindViewHolder(viewhold viewHolder, int position) {





        switch (position) {
            case 0:
               Glide.with(viewHolder.itemView)
                       .asBitmap()
                       .load("https://images.pexels.com/photos/218983/pexels-photo-218983.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260")
                       .into(viewHolder.imageView);
               viewHolder.textView.setText("   Night is to see the dreams\nand day is to make them true");

                break;
            case 1:
                Glide.with(viewHolder.itemView)
                        .load("https://images.pexels.com/photos/747964/pexels-photo-747964.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260")
                        .into(viewHolder.imageView);
                viewHolder.textView.setText("Wherever life plants you\n     bloom with grace");
                break;
            case 2:
                Glide.with(viewHolder.itemView)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.imageView);
                viewHolder.textView.setText(" Life is a journey\nnot a destination");
                break;
            case 3:
                Glide.with(viewHolder.itemView)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.imageView);
                viewHolder.textView.setText("Whatever you do, do it well ");
                break;

            default:
                Glide.with(viewHolder.itemView)
                         .load("data:image/jpeg;base64,")
                        .into(viewHolder.imageView);
                viewHolder.textView.setText("Every end is a new beginning");
                break;

        }

    }


    @Override
    public int getCount() {
        return 5;
    }


   public class viewhold extends SliderViewAdapter.ViewHolder{

        ImageView imageView;
        View itemView;
        TextView textView;


       public viewhold(View itemView) {
           super(itemView);
        imageView = itemView.findViewById(R.id.imageui);
        textView = itemView.findViewById(R.id.textui);

        this.itemView = itemView;

       }
   }
}
